,{{{{{ {}, {}, {} }}}}}-- The table that holds the datalocal table = {}-- Function that stores the temporary datafunction setTemporaryData( id, data )	if not ( id ) or not ( data ) then return false end	table[ id ] = data	outputDebugString( "(CLIENT) Cache backup kicked in for data with ID: " .. id .. " Type: ".. type( data ) )	return trueend-- Function that gets the temporary datafunction getTemporaryData( id )	if not ( id ) then return false end	if ( table[ id ] ) then		outputDebugString( "(CLIENT) Cache backup kicked to return data with ID: " .. id .. "" )		local n = table[ id ]		removeTemporaryData( id )		return n	else		return false	endend-- Function that removes the temporary datafunction removeTemporaryData( id )	if not ( id ) then return false end	if ( table[ id ] ) then		table[ id ] = nil		return true	else		return false	endend